/* https://leetcode.com/problems/find-k-th-smallest-pair-distance/description/
Given an integer array, return the k-th smallest distance among all the pairs. The distance of a pair (A, B) is defined as the absolute difference between A and B.
e.g.,
Input:
nums = [1,3,1]
k = 1
Output: 0 
Explanation:
Here are all the pairs:
(1,3) -> 2
(1,1) -> 0
(3,1) -> 2
Then the 1st smallest distance pair is (1,1), and its distance is 0.
*/
#include <algorithm>
#include <iostream>
#include <map>
#include <queue>
#include <vector>
using namespace std;
class Solution {
public:
	int smallestDistancePair(vector<int>& nums, int k) {
		sort(nums.begin(), nums.end());
		int i, j, c = 0, n = nums.size();
    int mi,lo=0,hi=nums[n-1]-nums[0];
    for (c=0; lo<hi; c=0) {
      mi=lo+(hi-lo)/2;
      for (i=0,j=0; i<n; ++i) { // tricky: i...j
        while (j<n && nums[j]-nums[i]<=mi) ++j;   // Wow, this is faster, O(nlog(n))
        c+=j-i-1; // think i=j=0
      }
      if (c<k)
        lo=mi+1;
      else
        hi=mi;
    }
    return lo;
  }
	int smallestDistancePair1(vector<int>& nums, int k) {
		std::sort(nums.begin(), nums.end());
		int i, j, c = 0, n = nums.size();
		int mi, lo=0, hi=nums[n-1]-nums[0]; // lo,hi,mi are in distance domain
		for (c=0; lo<hi; c=0) {
			mi=lo+(hi-lo)/2;
			for (i=0,j=n-1; i<n; ++i,j=n-1) { // i stepping, j restarts from n-1, poor O(n^2)
				while (j>i && (nums[j]-nums[i] > mi)) --j;
				c += j-i;
			}
			if (c < k)
				lo=mi+1;
			else
				hi=mi;
		}
		return lo;
	}
  // O(n^2)
	int smallestDistancePair2(vector<int>& nums, int k) {
		std::vector<int> v;
		for (int i=0; i<nums.size(); ++i) {
			for (int j=i+1; j<nums.size(); ++j)
				v.push_back(abs(nums[i]-nums[j]));
		}
		std::sort(v.begin(), v.end());  // O(nlog(n))
		return v[k-1];
	}
};
