#include <bits/stdc++.h>
using namespace std;
class Solution2 {
public: // MLE
  string longestDupSubstring(string S) {
    int n=S.size();
    string res;
    for (int i=n-1;i>0;--i) {     // len
      unordered_set<string> st;
      for (int j=0;j<=n-i;++j) {  // start
        string s=S.substr(j,i);
        if (st.count(s))
          return s;
        st.insert(s);
      }
    }
    return "";
  }
};
class Solution {
public: // TLE
  string longestDupSubstring(string S) {
    int n=S.size();
    string res;
    for (int i=n-1;i>0;--i) {     // len
      unordered_set<string> st;
      for (int j=0;j<=n-i;++j) {  // start
        string s=S.substr(j,i);
        for (auto ss: st) {
          if (ss.find(s)!=string::npos)
            return s;
        }
        st.insert(s);
      }
    }
    return "";
  }
};
int main(){
  Solution2 s;
  cout << s.longestDupSubstring("banana") << endl;
  cout << s.longestDupSubstring("abcd") << endl;
  cout << s.longestDupSubstring("") << endl;
}
